import React from "react";
import { useState } from 'react';

const TickerInput = ({ onAddTicker }) => {
    const [tickername, setTickername] = useState('');
    const [isstock, setIsStock] = useState(true);
    
    const onSubmit = (e) => {
        e.preventDefault();
        if (!tickername){
            alert('please add ticker')
            return
        }
        onAddTicker({tickername, isstock})
        setTickername('')
        setIsStock(true)
    }

  return (
      <form onSubmit={onSubmit}> 
          <div>
              <label htmlFor="">Ticker name</label>
              <input type="text" placeholder="add ticker" value = {tickername} onChange={(e) => setTickername(e.target.value)} />
          </div>
          <div>
              <label htmlFor="">Stock?</label>
              <input type='checkbox' checked={isstock} value={isstock} onChange={(e) => setIsStock(e.currentTarget.checked)}
        />
          </div>
          <input type="submit" value="add"/>
      </form>

  );
};

export default TickerInput;
