import React, { useState, useEffect } from "react";

const Clock = () => {
  const [date, setDate] = useState(new Date());

  useEffect(() => {
    var timerID = setInterval(() => {
      tick();
    }, 1000);
    return function cleanup() {
      clearInterval(timerID);
    };
  }, [date]);

  function tick() {
    setDate(new Date());
  }
  return (
    <div>
      <h2 className="root">It is {date.toLocaleTimeString()}.</h2>
      <h4>{price}</h4>
    </div>
  );
};
