import { useState, useEffect } from "react";
import TickerInput from "./components/TickerInput";
import Tickers from "./components/Tickers";

function App() {
  const [tickers, setTickers] = useState([]);

  useEffect(() => {
    const getTickers = async () => {
      const tickersFromServer = await fetchTickers();
      setTickers(tickersFromServer);
    };
    fetchTickers();
  }, []);

  //Fetch Tickers

  const fetchTickers = async () => {
    const res = await fetch("http://localhost:5000/tickers");
    const data = await res.json();
    console.log(data);
    return data;
  };

  //Add Ticker
  const addTicker = (ticker) => {
    const id = Math.floor(Math.random() * 10000) + 1;
    const newTicker = { id, ...ticker };
    setTickers([...tickers, newTicker]);
  };

  return (
    <div className="App">
      <TickerInput onAddTicker={addTicker} />
      <Tickers tickers={tickers}></Tickers>
    </div>
  );
}

export default App;
